import timeimport ledfrom wifi import WiFi# noinspection PyUnresolvedReferencesclass RunLoop:    def __init__(self, config, verbose=0):        self.exit = False        self.verbose = verbose        self.config = config        self.led = led.Led(config['pinout']['led'])        self.wifi = WiFi(config, verbose=verbose)        if self.verbose:            print('<{}>'.format(config['device']['name']))            print(str(self.led))            print(str(self.wifi))        # Application ready feedback --------------------------------------------------------------------------------- #        self.led.on(poll=True)        time.sleep(2)        self.led.off(poll=True)        # ------------------------------------------------------------------------------------------------------------ #    def run(self):        if self.verbose:            print('Run loop started')        while not self.exit:            # =============================== #            self.led.poll()            # =============================== #            self.wifi.connect()            if self.wifi.connected():                # Connected feedback                self.led.toggle(500)            elif self.wifi.connecting():                # Connecting feedback                self.led.toggle(250)            # =============================== #            # Reduce the tightness of the loop            time.sleep_ms(20)        if self.verbose:            print('Run loop exited')    def close(self):        self.exit = True        self.led.close()        if self.verbose:            print('Run loop closed')